import React, { Component } from "react";
import { Section, StyledButton } from "../Components";
import Card from "../Components/Card";
import Tab from "../Components/Tabs";
import { Button } from "../Components";

import { Card as SemanticCard } from "semantic-ui-react";

const secondSection = [
  {
    id: 0,
    img: require("../assets/public.svg"),
    title: "Public Q&A",
    message:
      "Get answers to more than 16.5 million questions and give back by sharing your knowledge with others. Sign up for an account.",
    action: "Browse questions",
  },
  {
    id: 1,
    img: require("../assets/public.svg"),
    title: "Private Q&A",
    message:
      "Level up with Stack Overflow while you work. Share knowledge privately with your coworkers using our flagship Q&A engine.",
    action: "Try for free",
  },
  {
    id: 2,
    img: require("../assets/public.svg"),
    title: "Browse jobs",
    message:
      "Find the right job through high quality listings and search for roles based on title, technology stack, salary, location, and more.",
    action: "Find a job",
  },
];

const secondSectionTab = [
  {
    id: 0,
    img: require("../assets/private-questions.svg"),
    message: "Quickly find and share internal knowledge with Private Q&A.",
  },
  {
    id: 1,
    img: require("../assets/find-candidate.svg"),
    message:
      "Find the perfect candidate for your growing technical team with Talent solutions",
  },
  {
    id: 2,
    img: require("../assets/accelerate.svg"),
    message:
      "Accelerate the discovery of your products or services through our Advertising platform",
  },
];

const fifth = [
  {
    id: 0,
    img: require("../assets/jobs-tech.svg"),
    message: "Browse jobs by technology",
  },
  {
    id: 1,
    img: require("../assets/jobs-salary.svg"),
    message: "Browse jobs by salary",
  },
  {
    id: 2,
    img: require("../assets/jobs-visa.svg"),
    message: "Browse jobs by visa sponsorship",
  },
  {
    id: 3,
    img: require("../assets/jobs-remote.svg"),
    message: "Browse remote-friendly jobs",
  },
];

class Homepage extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    return (
      <>
        <div>
          <Section primary>
            <div align="center">
              <div
                style={{ fontSize: "3em", fontWeight: "bold" }}
              >{`We <3 people who code`}</div>
              <div
                style={{
                  fontSize: "1.3em",
                  maxWidth: "350px",
                  textAlign: "center",
                  margin: "2%",
                }}
              >
                We build products that empower developers and connect them to
                solutions that enable productivity, growth, and discovery.
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  flex: 1,
                  justifyContent: "center",
                }}
              >
                <StyledButton developer> For Developers</StyledButton>
                <StyledButton> For Buisness</StyledButton>
              </div>
            </div>
          </Section>
        </div>
        <div>
          <Section secondary>
            <div align="center">
              <div
                style={{ fontSize: "2em", fontWeight: "bold" }}
              >{`For developers, by developers`}</div>
              <div
                style={{
                  width: "70px",
                  height: "10px",
                  background: "#f28033",
                  borderRadius: "5px",
                  marginTop: "2%",
                }}
              ></div>
              <div
                style={{
                  fontSize: "1.1em",
                  maxWidth: "550px",
                  textAlign: "center",
                  margin: "2%",
                }}
              >
                Stack Overflow is an open community for anyone that codes. We
                help you get answers to your toughest coding questions, share
                knowledge with your coworkers in private, and find your next
                dream job.
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  flex: 1,
                  justifyContent: "space-evenly",
                  alignItems: "center",
                }}
              >
                {secondSection.map((card, index) => (
                  <Card
                    number={card.id}
                    img={card.img}
                    title={card.title}
                    message={card.message}
                    action={card.action}
                  />
                ))}
              </div>
            </div>
            <div align="center">
              <div
                style={{ fontSize: "2em", fontWeight: "bold" }}
              >{`For businesses, by developers`}</div>
              <div
                style={{
                  width: "70px",
                  height: "10px",
                  background: "#f28033",
                  borderRadius: "5px",
                  marginTop: "2%",
                }}
              ></div>
              <div
                style={{
                  fontSize: "1.1em",
                  maxWidth: "550px",
                  textAlign: "center",
                  margin: "2%",
                }}
              >
                Our mission is to help developers write the script of the
                future. This means helping you find and hire skilled developers
                for your business and providing them the tools they need to
                share knowledge and work effectively.
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  flex: 1,
                  justifyContent: "space-evenly",
                  alignItems: "center",
                }}
              >
                {secondSectionTab.map((card, index) => (
                  <Tab number={card.id} img={card.img} message={card.message} />
                ))}
              </div>
            </div>
          </Section>
        </div>
        <div>
          <Section third>
            <div style={{ color: "#fff" }} align="center">
              <div
                style={{
                  fontSize: "2.5em",
                  fontWeight: "bold",
                  lineHeight: "40px",
                  maxWidth: "500px",
                  width: "100%",
                  wordBreak: "break-all",
                  // height: "200px",
                }}
              >{`Unlock siloed knowledge with Stack Overflow for Teams`}</div>
              <div
                style={{
                  fontSize: "1.3em",
                  maxWidth: "350px",
                  textAlign: "center",
                  margin: "2%",
                }}
              >
                Wikis, chat messages, or formal documentation for knowledge
                management aren’t effective. Our question and answer format is a
                proven approach for accessing the right information in less
                time.
              </div>

              <Button> Learn More</Button>
            </div>
          </Section>
        </div>
        <div>
          <Section secondary>
            <div style={{ display: "flex", flexDirection: "row", flex: 1 }}>
              <div
                style={{
                  display: "flex",
                  flex: 0.5,
                  flexDirection: "column",
                  width: "400px",
                }}
              >
                <img
                  style={{ width: "50px", height: "50px" }}
                  src={require("../assets/find-candidate.svg")}
                />
                <div>Hire your technical talent</div>
                <div style={{ width: "400px" }}>
                  We help expand your technical hiring strategy to promote your
                  employer brand and highlight relevant open roles to our
                  community of 50 million monthly visitors.
                </div>
                <Button style={{ width: "200px" }} secondary>
                  Stack Overflow Talent
                </Button>
              </div>
              <div
                style={{
                  display: "flex",
                  flex: 0.5,
                  flexDirection: "column",
                  width: "400px",
                  justifyContent:"Center"
                }}
              >
                <img
                  style={{ width: "50px", height: "50px" }}
                  src={require("../assets/accelerate.svg")}
                />
                <div>Reach developers worldwide</div>
                <div style={{ width: "400px" }}>
                  Use the world’s largest resource for people who code to help
                  you increase awareness and showcase your product or service
                  across Stack Overflow’s network of Q&A sites.
                </div>
                <Button style={{ width: "200px" }} secondary>
                  Stack Overflow Advertising
                </Button>
              </div>
            </div>
          </Section>
        </div>
        <div>
          <Section fifth>
            <div style={{ paddingRight: "15%", paddingLeft: "15%" }}>
              <div
                style={{
                  fontWeight: "bold",
                  fontSize: "35px",
                  marginBottom: "2%",
                }}
              >
                Looking for a job?
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  flex: 1,
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                {fifth.map((card, index) => (
                  <Tab
                    fifth={true}
                    number={card.id}
                    img={card.img}
                    message={card.message}
                  />
                ))}
              </div>
            </div>
          </Section>
        </div>
      </>
    );
  }
}

