import React, { Component } from "react";

const left = [
  {
    number: 0,
    img: require("../assets/ask.svg"),
    title: "Ask a question",
  },
  {
    number: 1,
    img: require("../assets/vote.svg"),
    title: "Vote on evrything",
  },
  {
    number: 2,
    img: require("../assets/answer.svg"),
    title: "Answer questions",
  },
  {
    number: 3,
    img: require("../assets/tags.svg"),
    title: "Tag your question",
  },
  {
    number: 4,
    img: require("../assets/accept.svg"),
    title: "Accept an answer",
  },
  {
    number: 5,
    img: require("../assets/recognize.svg"),
    title: "Get recognized",
  },
];

class Animation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      number: 0,
      image: null,
    };
  }

  componentDidMount() {
    this.interval = setInterval(() => {
      let number = this.state.number;
      this.setState({
        image: left[number].img,
        number: this.state.number < 5 ? number + 1 : 0,
      });
    }, 2000);
  }
  componentWillUnmount() {
    clearInterval(this.interval);
  }

  handleAnimate = (number) => {
    this.setState({
      number: number,
      image: left[number].img,
    });
  };

  handleMouseOver = (number) => {
    this.setState({
      number: number,
    });
  };

  render() {
    return (
      <>
        <div
          align="center"
          style={{
            display: "flex",
            flexDirection: "row",
            flex: 1,
            justifyContent: "center",
          }}
        >
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-evenly",
              flex: 0.2,
            }}
          >
            {left.map((item) =>
              item.number !== 3 && item.number !== 4 && item.number !== 5 ? (
                <div
                  onMouseOver={() => this.handleMouseOver(item.number)}
                  style={{
                    border: "1px solid black",
                    padding: "4%",
                    width: " 200px",
                  }}
                  onClick={() => this.handleAnimate(item.number)}
                >
                  {item.title}
                </div>
              ) : null
            )}
          </div>
          <div
            align="center"
            style={{
              display: "flex",
              flexDirection: "column",
              width: "250px",
              height: "300px",
              justifyContent: "center",
              alignItems: "center",
              flex: 0.2,
            }}
          >
            <img src={this.state.image} />
          </div>
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-evenly",
              alignItems: "center",
              flex: 0.2,
            }}
          >
            {left.map((item) =>
              item.number !== 0 && item.number !== 1 && item.number !== 2 ? (
                <div
                  onMouseOver={() => this.handleMouseOver(item.number)}
                  style={{
                    border: "1px solid black",
                    padding: "4%",
                    width: " 200px",
                  }}
                  onClick={() => this.handleAnimate(item.number)}
                >
                  {item.title}
                </div>
              ) : null
            )}
          </div>
        </div>
      </>
    );
  }
}
export default Animation;
