import styled from "styled-components";

export const Section = styled.div`
  height: auto;
  width: 100%;
  background: ${(props) => (props.primary ? "#fecf38" : "#fff")};
  padding-top: 128px !important;
  padding-bottom: 128px !important;
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

export const StyledButton = styled.button`
padding-left :24px'
padding-right: 24px;
padding-top:12px;
padding-bottom:12px;
background-color: ${(props) => (props.developer ? "#fff" : "#f28033")};
color:  ${(props) => (props.developer ? "#f28033" : "#fff")};
border:  ${(props) => (props.developer ? "0.5px solid #f28033" : "#fff")};
`;

export const Button = styled.button`
padding-left :24px'
padding-right: 24px;
padding-top:12px;
padding-bottom:12px;
background-color : ${(props) => (props.primary ? "#2b2d6e" : "#000")}; 
`;
