import React, { Component } from "react";
import { Section, StyledButton } from "../Components";
import Card from "../Components/Card";
import Tab from "../Components/Tabs";
import { Button } from "../Components";
import Animation from "../Animation";
import Learn from "../Learn";
import Package from "../Package";
import "./homepage.css";
import SecondSection from "../SecondSection"

const secondSection = [
  {
    id: 0,
    img: require("../assets/public.svg"),
    title: "Public Q&A",
    message:
      "Get answers to more than 16.5 million questions and give back by sharing your knowledge with others. Sign up for an account.",
    action: "Browse questions",
  },
  {
    id: 1,
    img: require("../assets/public.svg"),
    title: "Private Q&A",
    message:
      "Level up with Stack Overflow while you work. Share knowledge privately with your coworkers using our flagship Q&A engine.",
    action: "Try for free",
  },
  {
    id: 2,
    img: require("../assets/public.svg"),
    title: "Browse jobs",
    message:
      "Find the right job through high quality listings and search for roles based on title, technology stack, salary, location, and more.",
    action: "Find a job",
  },
];

const secondSectionTab = [
  {
    id: 0,
    img: require("../assets/private-questions.svg"),
    message: "Quickly find and share internal knowledge with Private Q&A.",
  },
  {
    id: 1,
    img: require("../assets/find-candidate.svg"),
    message:
      "Find the perfect candidate for your growing technical team with Talent solutions",
  },
  {
    id: 2,
    img: require("../assets/accelerate.svg"),
    message:
      "Accelerate the discovery of your products or services through our Advertising platform",
  },
];

const fifth = [
  {
    id: 0,
    img: require("../assets/jobs-tech.svg"),
    message: "Browse jobs by technology",
  },
  {
    id: 1,
    img: require("../assets/jobs-salary.svg"),
    message: "Browse jobs by salary",
  },
  {
    id: 2,
    img: require("../assets/jobs-visa.svg"),
    message: "Browse jobs by visa sponsorship",
  },
  {
    id: 3,
    img: require("../assets/jobs-remote.svg"),
    message: "Browse remote-friendly jobs",
  },
];

class Homepage extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    return (
      <>
        <div>
          <Section primary>
            <div align="center">
              <div className="sectionHeading">{`We <3 people who code`}</div>
              <div
                style={{
                  fontSize: "1.3em",
                  maxWidth: "350px",
                  textAlign: "center",
                  margin: "2%",
                }}
              >
                We build products that empower developers and connect them to
                solutions that enable productivity, growth, and discovery.
              </div>
              <div classname="section1Button">
                <StyledButton developer> For Developers</StyledButton>
                <StyledButton> For Buisness</StyledButton>
              </div>
            </div>
          </Section>
        </div>
        <SecondSection />
        <div>
          <Section third>
            <div style={{ color: "#fff" }} align="center">
              <div
                style={{
                  fontSize: "2.5em",
                  fontWeight: "bold",
                  lineHeight: "40px",
                  maxWidth: "500px",
                  width: "100%",
                  wordBreak: "break-all",
                  // height: "200px",
                }}
              >{`Unlock siloed knowledge with Stack Overflow for Teams`}</div>
              <div
                style={{
                  fontSize: "1.3em",
                  maxWidth: "350px",
                  textAlign: "center",
                  margin: "2%",
                }}
              >
                Wikis, chat messages, or formal documentation for knowledge
                management aren’t effective. Our question and answer format is a
                proven approach for accessing the right information in less
                time.
              </div>

              <Button> Learn More</Button>
            </div>
            <div>
              <Package />
            </div>
          </Section>
        </div>
        <div>
          <Section secondary>
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                flex: 1,
                paddingTop: "5%",
                paddingBottom: "5%",
              }}
            >
              <div
                style={{
                  display: "flex",
                  flex: 0.5,
                  flexDirection: "column",
                  width: "400px",
                  marginLeft: "20%",
                }}
              >
                <img
                  style={{ width: "80px", height: "80px" }}
                  src={require("../assets/find-candidate.svg")}
                />
                <div
                  style={{
                    fontWeight: "bold",
                    fontSize: "2em",
                    marginBottom: "2%",
                  }}
                >
                  Hire your technical talent
                </div>
                <div style={{ width: "400px", marginBottom: "2%" }}>
                  We help expand your technical hiring strategy to promote your
                  employer brand and highlight relevant open roles to our
                  community of 50 million monthly visitors.
                </div>
                <Button style={{ width: "200px" }} secondary>
                  Stack Overflow Talent
                </Button>
              </div>
              <div
                style={{
                  display: "flex",
                  flex: 0.5,
                  flexDirection: "column",
                  width: "400px",
                  alignItems: "flex-start",
                }}
              >
                <img
                  style={{ width: "80px", height: "80px" }}
                  src={require("../assets/accelerate.svg")}
                />
                <div
                  style={{
                    fontWeight: "bold",
                    fontSize: "2em",
                    marginBottom: "2%",
                  }}
                >
                  Reach developers worldwide
                </div>
                <div style={{ width: "400px", marginBottom: "2%" }}>
                  Use the world’s largest resource for people who code to help
                  you increase awareness and showcase your product or service
                  across Stack Overflow’s network of Q&A sites.
                </div>
                <Button style={{ width: "200px" }} secondary>
                  Stack Overflow Advertising
                </Button>
              </div>
            </div>
          </Section>
        </div>
        <div align="center">
          <Section>
            <Animation />
          </Section>
        </div>
        <div align="center">
          <Section>
            <Learn />
          </Section>
        </div>
        <div>
          <Section fifth>
            <div style={{ paddingRight: "15%", paddingLeft: "15%" }}>
              <div
                style={{
                  fontWeight: "bold",
                  fontSize: "35px",
                  marginBottom: "2%",
                }}
              >
                Looking for a job?
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  flex: 1,
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                {fifth.map((card, index) => (
                  <Tab
                    fifth={true}
                    number={card.id}
                    img={card.img}
                    message={card.message}
                  />
                ))}
              </div>
            </div>
          </Section>
        </div>
      </>
    );
  }
}

export default Homepage;
