import { put, call, takeLatest, take } from "redux-saga/effects";

export const START_RECHARGE = "START_RECHARGE";
export const SAVE_RECHARGE = "SAVE_RECHARGE";

export const START_SUBSCRIBE_PACK = "START_SUBSCRIBE_PACK";
export const SAVE_SUBSCRIBE_PACK = "SAVE_SUBSCRIBE_PACK";

export const START_ADD_CHANNELS = "START_ADD_CHANNELS";
export const SAVE_ADD_CHANNELS = "SAVE_ADD_CHANNELS";

export const ADD_EMAIL = "ADD_EMAIL";
export const SAVE_EMAIL = "SAVE_EMAIL";

export const ADD_NUMBER = "ADD_NUMBER";
export const SAVE_NUMBER = "SAVE_NUMBER";

export const addNumber = (payload) => ({
  type: "ADD_NUMBER",
  payload,
});
export const addChannel = (payload) => ({
  type: "START_ADD_CHANNELS",
  payload,
});

export const startRecharge = (payload) => ({
  type: "START_RECHARGE",
  payload,
});
export const startSubscribePack = (payload) => ({
  type: "START_SUBSCRIBE_PACK",
  payload,
});

export const addEmail = (payload) => ({
  type: "ADD_EMAIL",
  payload,
});
export default function* rootSaga() {
  yield takeLatest("START_RECHARGE", handleRecharge);
  yield takeLatest("START_SUBSCRIBE_PACK", handleSubscribePack);
  yield takeLatest("START_ADD_CHANNELS", handleAddChannel);
  yield takeLatest("ADD_EMAIL", handleAddEmail);
  yield takeLatest("ADD_NUMBER", handleAddNumber);

}

function* handleRecharge(action) {
  console.log("action", action.payload);
  try {
    const recharge = action.payload;
    yield put({
      type: "SAVE_RECHARGE",
      recharge,
    });
  } catch (error) {
    yield put({
      type: "SAVE_RECHARGE",
      error,
    });
  }
}
function* handleSubscribePack(action) {
  console.log("action", action.payload);
  try {
    const subscribePack = action.payload;
    yield put({
      type: "SAVE_SUBSCRIBE_PACK",
      subscribePack,
    });
  } catch (error) {
    yield put({
      type: "SAVE_SUBSCRIBE_PACK",
      error,
    });
  }
}

function* handleAddChannel(action) {
  console.log("action", action.payload);
  try {
    const channels = action.payload;
    yield put({
      type: "SAVE_ADD_CHANNELS",
      channels,
    });
  } catch (error) {
    yield put({
      type: "SAVE_ADD_CHANNELS",
      error,
    });
  }
}

function* handleAddEmail(action) {
  console.log("action", action.payload);
  try {
    const email = action.payload;
    yield put({
      type: "SAVE_EMAIL",
      email,
    });
  } catch (error) {
    yield put({
      type: "SAVE_EMAIL",
      error,
    });
  }
}


function* handleAddNumber(action) {
    console.log("action", action.payload);
    try {
      const number = action.payload;
      yield put({
        type: "SAVE_NUMBER",
        number,
      });
    } catch (error) {
      yield put({
        type: "SAVE_NUMBER",
        error,
      });
    }
  }
