import React, { Component } from "react";

const left = [
  {
    number: 0,
    img: require("../assets/ask.svg"),
    title: "Ask a question",
  },
  {
    number: 1,
    img: require("../assets/vote.svg"),
    title: "Vote on evrything",
  },
  {
    number: 2,
    img: require("../assets/answer.svg"),
    title: "Answer questions",
  },
  {
    number: 3,
    img: require("../assets/tags.svg"),
    title: "Tag your question",
  },
  {
    number: 4,
    img: require("../assets/accept.svg"),
    title: "Accept an answer",
  },
  {
    number: 5,
    img: require("../assets/recognize.svg"),
    title: "Get recognized",
  },
];

class Animation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      number: null,
      image: null,
    };
  }

  //   componentDidMount() {
  //     setTimeout(() => {
  //         left.map(item => {
  //             this.setState({
  //                 image : item.img
  //             })
  //         })
  //     }, 1000);
  //   }

  //   componentDidMount() {}
  //   componentWillUnmount() {
  //     clearInterval(this.interval);
  //   }

  //   animate = () => {
  //     this.interval = setInterval(() => {
  //       left.map((item) => {
  //         if (this.state.number !== item.number) {
  //           this.setState({
  //             image: item.img,
  //             number: item.number,
  //           });
  //         }
  //       });
  //     }, 1000);
  //     this.interval =

  //     left.map()
  //     setInterval(() => this.setState({ time: Date.now() }), 1000);
  //   };

  componentDidMount() {
    // this.interval = left.map((item) => {
    //   setTimeout(
    //     () => this.setState({ image: item.img, number: item.number }),
    //     1000
    //   );
    // });
    this.setState({
      number: 0,
    });
  }
  componentWillUnmount() {
    // clearInterval(this.interval);
  }

  componentDidUpdate(prevState) {
    if (prevState.number !== this.state.number) {
      let number = this.state.number;
      setTimeout(() => {
        this.setState({
          image: left[number].image,
          number: number == 5 ? 0 : number + 1,
        });
      }, 2000);
    }
  }

  //   animate = () => {
  //     let number = this.state.number;
  //     // setTimeout(() => {
  //     //   this.setState({
  //     //     image: left[number].image,
  //     //     number: number == 5 ? 0 : number + 1,
  //     //   });
  //     // }, 2000);
  //     this.setState({
  //         image: left[0].image,
  //         number: number == 5 ? 0 : number + 1,
  //       });
  //   };

  render() {
    return (
      <>
        <div style={{ display: "flex", flexDirection: "row", flex: 1 }}>
          <div style={{ display: "flex", flexDirection: "column" }}>
            {left.map((item) =>
              item.number !== 3 && item.number !== 4 && item.number !== 5 ? (
                <div>{item.title}</div>
              ) : null
            )}
          </div>
          <div style={{ display: "flex", flexDirection: "column" }}>
            <img src={this.state.image} />
          </div>
          <div style={{ display: "flex", flexDirection: "column" }}>
            {left.map((item) =>
              item.number !== 0 && item.number !== 1 && item.number !== 2 ? (
                <div>{item.title}</div>
              ) : null
            )}
          </div>
          {/* {this.animate()} */}
        </div>
      </>
    );
  }
}
export default Animation;
