import React, { Component } from "react";
import styled from "styled-components";
import { Button } from "../Components/index";

const packages = [
  {
    id: 0,
    rate: "$5",
    time: "per teammate / month billed annually",
    title: "Basic",
    message: "Private knowledge base for teams",
    offers: [
      "Free until June 30",
      "Your own private space hosted on stackoverflow.com",
      "Fully searchable archive",
    ],
  },
  {
    id: 1,
    rate: "$12",
    time: "per teammate / month billed annually",
    title: "Business",
    message: "Private knowledge base with SSO and premium features",
    offers: [
      "All the features of Basic",
      "Single sign-on (SSO) with SAML",
      "Reporting and analytics",
      "Priority customer support",
      "99.5% uptime",
    ],
  },
  {
    id: 2,
    rate: "Custom pricing",
    time: "",
    title: "Enterprise",
    message:
      "Standalone knowledge base with enhanced security and flexible hosting",
    offers: [
      "Host on your cloud or servers â€“ or our private managed cloud",
      "Single sign-on with AD or SAML",
      "Robust read and write API",
      "Your own customer success and community building representative",
      "99.5% uptime SLA and priority support",
    ],
  },
];

class Package extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    return (
      <>
        <div
          style={{
            display: "flex",
            flexDirection: "row",
            flex: 1,
            justifyContent: "space-around",
            alignItems: "center",
            marginTop: "5%",
            padding: "2%",
          }}
        >
          {packages.map((item, index) => (
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                flex: 1,
                width: "100%",
                height: "100%",
                maxWidth: "402px",
                maxHeight: "604px",
                borderRadius: "10px",
                alignSelf: "flex-end",
                justifyContent: "space-evenly",
                background: item.id == 2 ? "none" : "#fff",
                color: item.id == 2 ? "#fff" : "inherit",
                border: item.id == 2 ? "0.5px solid #fff" : "none",
              }}
            >
              <div
                style={{
                  borderBottom: "0.2px solid grey",
                  padding: "32px",
                  display: "flex",
                  flex: 1,
                  alignItems: "center",
                  flexDirection: "column",
                  justifyContent: "center",
                }}
                align="center"
              >
                <div
                  style={{
                    color: item.id == 2 ? "#fff" : "#27348b",
                    paddingTop: "3%",
                    fontSize: "1.5em",
                    fontWeight: "bold",
                  }}
                >
                  {item.title}
                </div>
                <div
                  style={{
                    padding: "2%",
                    fontSize: "1.1em",
                    fontWeight: "normal",
                    color: item.id == 2 ? "#fff" : "inherit",
                  }}
                >
                  {item.message}
                </div>
              </div>
              <div style={{ padding: "32px" }}>
                <div style={{ lineHeight: "20px", verticalAlign: "middle" }}>
                  <span style={{ fontSize: "1.8rem", fontWeight: "bold" }}>
                    {item.rate}
                  </span>
                  <span> {item.time}</span>
                </div>
                <div>
                  {item.offers.map((offer) => (
                    <div style={{ lineHeight: "20px", paddingTop: "2%" }}>
                      <span style={{ color: "#f28033", fontSize: "1.5rem" }}>
                        &#x2713;
                      </span>
                      {offer}
                    </div>
                  ))}
                </div>
              </div>
              <div align="center">
                <Button
                  fourth={item.id == 0 || 1}
                  third={item.id == 2}
                  style={{ width: "250px", margin: "5%", color :"#000", fontWeight :"550"}}
                >
                  {" "}
                  {item.id == 0
                    ? "Get started for free"
                    : item.id == 1
                    ? "Request a demo"
                    : item.id == 2
                    ? "Request a demo"
                    : "none"}
                </Button>
              </div>
            </div>
          ))}
        </div>
      </>
    );
  }
}

export default Package;
// #ffb500;

