import styled from "styled-components";

export const Section = styled.div`
  height: auto;
  width: 100%;
  background: ${(props) => (props.primary ? "#fecf38" : props.secondary ? "#f7f6f9": props.fifth ? "##e1ecf4" : "#fff")};
  padding-top: 64px !important;
  padding-bottom: 64px !important;
  display: flex;
  flex: 1;
  flex-direction: column;
`;  

export const StyledButton = styled.button`
  background-color: ${(props) => (props.developer ? "#fff" : "#f28033")};
  color: ${(props) => (props.developer ? "#f28033" : "#fff")};
  border: ${(props) => (props.developer ? "0.5px solid #f28033" : "#fff")};
  padding: 1em 1em;
`;

export const Button = styled.button`
  background: ${(props) => (props.primary ? "#2b2d6e" : "#f28033")};
  border: ${(props) => (props.primary ? "0.5px solid #2b2d6e" : "0.5px solid #f28033")};
  padding: 1em 1em;
  color: #fff;
  border-radius:5px;
  min-width :120px
`;
