import {
  SAVE_RECHARGE,
  SAVE_SUBSCRIBE_PACK,
  SAVE_ADD_CHANNELS,
  SAVE_EMAIL,
  SAVE_NUMBER,
} from "../Saga/index";

const initialState = {
  balance: 100,
  subscriptionType: "",
  channels: "",
  email: "",
  number: "",
  region: "USA",
  currencies: {
    USA: {
      currency: "$",
    },
    JAPAN: {
      currency: "Yen",
    },
  },
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case SAVE_RECHARGE:
      return {
        ...state,
        balance: action.recharge,
      };

    case SAVE_SUBSCRIBE_PACK: {
      return {
        ...state,
        subscriptionType: action.subscribePack,
      };
    }

    case SAVE_ADD_CHANNELS:
      return {
        ...state,
        channels: action.channels,
      };
    case SAVE_EMAIL:
      return {
        ...state,
        email: action.email,
      };
    case SAVE_NUMBER:
      return {
        ...state,
        number: action.number,
      };
    default:
      return state;
  }
};

export default reducer;
